```python

def es_primo(n):
    if n < 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def nth_prime_number(n):
    count = 0
    candidate = 2
    while True:
        if es_primo(candidate):
            count += 1
            if count == n:
                return candidate
        candidate += 1

