#include <utility>

// Function to return the largest pair in a map based on the second value of each pair
std::pair<int, int> largest_pair(const std::map<int, int>& my_map) {
    std::pair<int, int> largest_pair = *my_map.begin();
    for (auto& entry : my_map) {
        if (entry.second > largest_pair.second) {
            largest_pair = entry;
        }
    }
    return largest_pair;
}
```

